import{_ as e,c as r,o as d,aj as o}from"./chunks/framework.CynFBi0q.js";const _=JSON.parse('{"title":"读书","description":"","frontmatter":{},"headers":[],"relativePath":"good-life/read/index.md","filePath":"good-life/read/index.md","lastUpdated":1706456551000}'),i={name:"good-life/read/index.md"};function t(n,a,l,c,h,s){return d(),r("div",null,[...a[0]||(a[0]=[o('<h1 id="读书" tabindex="-1">读书 <a class="header-anchor" href="#读书" aria-label="Permalink to “读书”">​</a></h1><h2 id="什么叫做读书" tabindex="-1">什么叫做读书 <a class="header-anchor" href="#什么叫做读书" aria-label="Permalink to “什么叫做读书”">​</a></h2><p>中国的乡村文化里关于读书有经常会有这样的句子， <code>你要好好读书，以后找一份坐办公室不用风吹日晒的工作</code>、<code>你要好好读书，将来做个有出息的人</code>、<code>你要好好读书，长大了做个对社会有用的人</code>、<code>你要好好读书，未来......</code>。 这是中国改革开放后，九年义务教育等扫盲运动的时代背景下产生的言论。当然这里的读书应该来说只属于狭义的一种。</p><p>我理解的读书是获取信息和经验的手段，是通过阅读他人的人生经历丰富自己见识的途径，是贯通古今、跨越国际的故事来源。</p><h2 id="读什么书" tabindex="-1">读什么书 <a class="header-anchor" href="#读什么书" aria-label="Permalink to “读什么书”">​</a></h2><p>人物传记、历史故事、诗词文章、哲学思想、逻辑原理，心理变化、人性天性， 传授技能的书，描述世界原理的书，让生活更加美好的说，让精神充盈的书，</p><h2 id="怎么读书" tabindex="-1">怎么读书 <a class="header-anchor" href="#怎么读书" aria-label="Permalink to “怎么读书”">​</a></h2><p>按照顺序、结构化的串联和关联起来，在场景中应用。</p><h2 id="理解现实世界的原理" tabindex="-1">理解现实世界的原理 <a class="header-anchor" href="#理解现实世界的原理" aria-label="Permalink to “理解现实世界的原理”">​</a></h2><h2 id="道、天、地、将、法" tabindex="-1">道、天、地、将、法 <a class="header-anchor" href="#道、天、地、将、法" aria-label="Permalink to “道、天、地、将、法”">​</a></h2><p>专注力，一个时间单位内只做一件事情。</p>',11)])])}const f=e(i,[["render",t]]);export{_ as __pageData,f as default};
